CREATE TABLE magnitude_testing (
    id integer NOT NULL,
    big_num numeric,
    big_int integer,
    sm_num numeric,
    sm_dbl double precision,
    pm_seq integer GENERATED ALWAYS AS ((id - 100)) STORED,
    tens_seq integer GENERATED ALWAYS AS ((id * 10)) STORED
);

CREATE SEQUENCE magnitude_testing_id_seq
    AS integer
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1;

ALTER SEQUENCE magnitude_testing_id_seq OWNED BY magnitude_testing.id;

ALTER TABLE ONLY magnitude_testing
  ALTER COLUMN id SET DEFAULT nextval('magnitude_testing_id_seq'::regclass);


SELECT pg_catalog.setval('magnitude_testing_id_seq', 200, true);

ALTER TABLE ONLY magnitude_testing
    ADD CONSTRAINT magnitude_testing_pkey PRIMARY KEY (id);
